void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy)/iResolution.y;
    vec2 polarUV = vec2(atan(uv.x, uv.y), length(uv));

    vec3 textureSampling = texture(iChannel0, polarUV - (iTime + sin(iTime))).rgb;

    vec3 col = textureSampling;

    vec3 maskedValue = vec3(1.2f, 1.0f, 0.9f) - (col*4.5);

    fragColor = vec4(maskedValue, 1.0f);
}
