uniform float u_time;

void mainImage(out vec4 O, vec2 uv)
{
    float time = iTime;
    time = mod(time, 3.5);//comment out this line if you dont want it to repeat
    if (time > 3.5)
        O = vec4(0.0,0.0,0.0,0.0);
    else
    {
        vec2 p = uv.xy / iResolution.xy;
        float melt = time * 0.2;
        float stretch = sin(((p.x*0.5)) * 10.0) * time * 0.2;
        stretch += sin(((p.x*1.3)) * 10.0) * time * 0.1;
        stretch += sin(((p.x*2.4)) * 10.0) * time * 0.05;
        vec2 offset = vec2(0., (stretch * stretch) + ((melt * melt * melt) * 3.));
        p += offset;
        vec4 video = vec4(texture(iChannel0,p));
        if (p.y > 1.0 || p.x < 0.0 || p.x > 1.0 || p.y < 0.0)
            O = vec4(0.0,0.0,0.0,1.0);
        else
            O = video;
    }
}
