vec2 rotateUV(vec2 uv, float rotation, vec2 offset)
{
    return vec2(
        cos(rotation) * (uv.x - offset.x) + sin(rotation) * (uv.y - offset.y) + offset.x,
        cos(rotation) * (uv.y - offset.y) - sin(rotation) * (uv.x - offset.x) + offset.y
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 resolution_ratio = vec2(iResolution.x / iResolution.y, 1.0);
    vec2 offset_rot = vec2(0.5, 0.5) * resolution_ratio;

    vec2 uv = (fragCoord / iResolution.xy) * resolution_ratio;
    uv = rotateUV(uv, radians(iTime * 60.0), offset_rot) / resolution_ratio;

    fragColor = texture(iChannel0, uv);

    // Clipping borders
    //if (uv.x < 0.0 || uv.x > 1.0 || uv.y < 0.0 || uv.y > 1.0) {
    //    fragColor = vec4(0.0);
    //} else {
    //    fragColor = texture(iChannel0, uv);
    //}
}
