#define Gradient(out, arr, p) {           \
    float t = p * float(arr.length()-1);  \
    int i = int(t);                       \
    out = mix(arr[i], arr[i+1], fract(t));\
}

vec3 colors[] = vec3[](
  vec3(1,0,0),
  vec3(0,1,0),
  vec3(0,0,1)
);

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col;
    Gradient(col, colors, uv.x);

    //to srgb
    col = pow(col, vec3(1./2.2));

    fragColor = vec4(col,1);
}
