vec3 palette( float t ) {
    vec3 a = vec3(0.500, -0.500, 0.300);
    vec3 b = vec3(0.600, 0.500, 0.600);
    vec3 c = vec3(1.600, 1.000, 1.150);
    vec3 d = vec3(0.000, 0, 0.587);

    return a + b*cos( 6.28318*(c*t+d) );
}
float pi = 3.141592653589;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 FinalColor = vec3(0.0);
    float music = texture(iChannel0, vec2(1.0, 0.0)).x;

    for (float i = 0.0; i < 3.0; i++) {

        uv = fract(uv * ( ((sin(iTime * .6) + 1.0)* 0.25 + 0.5) + (iMouse.x / iResolution.x)* 0.5)) - 0.5;


        float d = length(uv) + music * 0.2;

        vec3 col = palette(length(uv0) + iTime * .6 + music * 0.5);

        d = sin(d * 12.0 + iTime) / 20.;
        d = abs(d);

        d = 0.02 / d;

        FinalColor = d * col;
    }

    fragColor = vec4(FinalColor, 1.0);
}
