#define PI 3.1415926535
float getDistance(vec2 a, float i, vec2 p){
    return (a.x*p.x + a.y*p.y + i)/sqrt(a.x*a.x+a.y*a.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy-vec2(.5, .5);
    uv.x*=iResolution.x/iResolution.y;
    float c = 0.;
    float d = distance(vec2(0.), uv);
    float steps = iTime;
    float angle = PI*(d-mod(d, 1./steps));
    for(float i = 0.; i < clamp(iTime, 1., 30.); i++){
        float sA = (PI/clamp(iTime, 1., 20.))*i;
        float d1 = getDistance(vec2(sin(angle+sA+iTime), cos(angle+sA+iTime)), 0., uv);
        float d2 = smoothstep(.02, 0., abs(d1));
        d = 1.-step(1., d);
        c+=d2*d;
    }
    fragColor = vec4(c*0.5*abs(cos(iTime/2.)), c*.5*abs(cos(iTime/5.)), c*.5*abs(cos(iTime/11.)), 1.0);
}
