uniform float openValue;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1) and center
    vec2 uv = (2.0*fragCoord - iResolution.xy) /iResolution.y;
    vec4 tex = texture(iChannel0,uv * 0.075);

    float value = fract(iTime)*0.5;

    uv = vec2(atan(uv.y,uv.x),length(uv));

    uv.x += iTime *0.7 ;

    // Control by open value

    // float ring = length(uv.y - mix(0.0,0.5,openValue);
    // float influneceDistance = smoothstep(mix(0.0,0.2,openValue)),0.7,uv.y);

    float ring = length(uv.y - 0.5);
    float influneceDistance = smoothstep(mix(0.0,0.2,1.),0.7,uv.y);

    //Version 1
    //float border = smoothstep(0.25,0.0,ring + abs(sin(uv.x * 3.0) * tex.x *0.2) * sin((0.4 *influneceDistance) * 10.0)); 

    //Version 2
    //float border = smoothstep(0.25,0.0,ring + abs(sin(uv.x*2. + tex.x *2.)) * influneceDistance* 0.08);

    //Version 3
    //float border = smoothstep(0.25,0.0,ring + abs(sin(uv.x*3. + tex.x *4.)) * (cos(16.*influneceDistance)* 0.065));

    //Version 4
    float border = smoothstep(0.25,0.0,ring + abs(sin(uv.x*2. + tex.x *3.)) * (cos(5.5*influneceDistance)* -0.08));



    vec3 col = mix(vec3(0.0,0.0,0.0),mix(vec3(0.0,0.558,0.799),vec3(0.1,0.3,1.0),smoothstep(1.0,0.3,border)),border);



    // Output to screen
    fragColor = vec4(col,1.0);
}
