// the number of colors in the image (min: 1, max: 255)
const float QUANTIZE = 4.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // high precision input
    vec3 color = uv.xxx * QUANTIZE;

    // sample white noise
    vec2 uvWhiteNoise = uv * (vec2(iResolution.xy) / vec2(textureSize(iChannel0, 0)));
    vec3 whiteNoise = texture(iChannel0, uvWhiteNoise).rgb; // three noise samples in [0, 1]

    // sample blue noise
    vec2 uvBlueNoise = uv * (vec2(iResolution.xy) / vec2(textureSize(iChannel1, 0)));
    vec3 blueNoise = texture(iChannel1, uvBlueNoise).rgb; // three noise samples in [0, 1]

    // counter floor error
    if (uv.y < .33)
    {
        color += .5;
    }

    // apply white noise
    else if (uv.y >= .33 && uv.y < .67)
    {
        color += whiteNoise.r;
    }

    // apply blue noise
    else if (uv.y >= .67)
    {
        color += blueNoise.r;
    }

    color = floor(color) / QUANTIZE;

    // borders
    if (abs(uv.y - .33) < .001 || 
        abs(uv.y - .67) < .001)
    {
        color = vec3(0);
    }

    fragColor = vec4(color, 1.0);
}
