#define r iResolution.xy
#define t iTime
vec2 rotate(vec2 p,float a){
	float c = cos(a);
    float s = sin(a);
    return p*mat2(c,s,-s,c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - r) / r.y;
    vec2 uv0 = uv;
    uv *= 2.;
    float t2 = t*0.5+300.;

    uv = rotate(uv,smoothstep(0.,.5,sin(length(uv)+t2)));

   for (float i = 0.; i<2.;i++){
        uv.x *= sin(t2+t2*length(uv)*0.01)*cos(t2*length(uv)*0.001);
        uv.y *= sin(t2*length(uv)*0.01)*cos(t2+t2*length(uv)*0.001);
    }

    float val = pow(.03/length(uv),1.5);
    val -= pow(length(uv0),100.)*2.;

    vec3 col = vec3(val+0.04,val+0.1,val+.3);   

    fragColor = vec4(col,1.0);
}
