#define H(a) (cos(radians(vec3(0, 60, 120))-((a)*6.2832))*.5+.5)
void mainImage( out vec4 C, in vec2 U )
{
    vec2 R = iResolution.xy,
         u = (U-.5*R)/R.y;
    vec3 c = texture(iChannel0, U/R, .2).rgb*.04;
    c = H(c+.4)*c+c;
    c *= H(1.-length(u))*.8+1.;
    C = vec4(c+c*c, 1);
}

#define P(u) .4*min(4., 1./length((fract(u-vec2(t*round(u.y/2.), t)+.5)-.5)/fwidth(u)/R.y*350.))/max(1., abs(u.y))
void mainImage(out vec4 C, in vec2 U)
{
    float t = -iTime/10.,
          b = (iMouse.z > 0.) ? .005: .0005;
    vec2 R = iResolution.xy,
         m = (iMouse.xy/R*4.)-2.;
    vec3 u = normalize(vec3((U-.5*R)/R.y, 1))*8.,
         c = texture(iChannel0, mix(U/R, vec2(.5), b)).rgb*.95;
    u = (u*u+u.yzx*u.yzx-1.)/u;
    u /= u.yzx;
    c += (P(u.yx) + P(u.zx)) * .4;
    u = max(abs(u), 1./abs(u))*sign(u);
    c += P(u.yx) + P(u.zx);
    c += (P(u.xy) + P(u.zy)) * .5;
    c += P(u.yz) + P(u.xz);
    C = vec4(c, 1);
}
