// Function to generate pseudorandom noise
float rand(vec2 co) {
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

// Noise function for creating scattered particle effect
float noise(vec2 p){
    vec2 ip = floor(p);
    vec2 u = fract(p);
    u = u*u*(3.0-2.0*u);

    float res = mix(
        mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
        mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
    return res*res;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 p = uv - 0.5;
    p.x *= iResolution.x / iResolution.y;

    float rad = length(p);
    float a = atan(p.y, p.x);
    float time = iTime;

    // create 3D effect
    float sphere = 0.5 + 0.9 * cos(25.0 * rad - 3.0 * time + a);
    sphere += 0.02 * sin(50.0 * rad - 5.0 * time + a);
    sphere *= 1.0 - 0.4 * rad;

    // create strobe light effect
    float strobe = 0.5 + 0.9 * sin(13.0 * rad - time);
    strobe += 0.02 * cos(50.0 * rad - 7.0 * time + a);
    strobe *= 1.2 - rad;

    // create star field
    vec3 color = vec3(strobe * sphere);

    // add red dust explosion
    vec2 n = noise(uv * iResolution.x) * vec2(3.14, 0.0);
    float dust = rand(n + vec2(0.0, iTime)) - 1.5;
    color += vec3(dust, 0.0, 0.2);

    fragColor = vec4(color, 1.0);
}

