void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 start1 = vec2( 250.0f, 200.0f);
    vec2 start2 = vec2( 450.0f, 200.0f);

    vec2 r1 = fragCoord - start1;
    vec2 r2 = fragCoord - start2;



    float y1 = 0.f;
    float l1 = 60.f; // Left Wavelength
    float T1 = 2.f; // Left Period
    float phi1 = iTime / T1 - length(r1) / l1;
    if (phi1 >= 0.f)
      y1 = sin(6.28 * phi1);

    float y2 = 0.f;
    float l2 = 30.f; // Right Wavelength
    float T2 = 1.f; // Right Period
    float phi2 = iTime / T2 - length(r2) / l2;
    if (phi2 >= 0.f)
      y2 = sin(6.28 * phi2);

    float y = y1 + y2;


    // Output to screen
    fragColor = vec4(0.5 * y, 0.0, -y * 0.5, 1.0);
}
