float spiralWave(vec2 p, float ratio, float rate, float scale) {

    float r = length(p);
    float theta = atan(p.x,p.y);

    float logspiral = log(r)/ratio + atan(theta);
    return sin(rate*iTime - scale*logspiral);
}


float spiralToEllipse(vec2 p, float ratio, float rate, float scale, float transitionRadius) {

    float r = length(p);
    float theta = atan(p.x,p.y);
    float mini = min( (r - transitionRadius*1.7), 0.1);

    float logspiral = log(r)/ratio/ mini + atan(theta);
    float spiralValue = tan(rate*iTime - scale*logspiral);

    if (r > transitionRadius) {
       // float ellipseValue = sin(rate*iTime - scale*r);
        float ellipseValue = -10.;
        float t = smoothstep(transitionRadius, transitionRadius + 0.1, r);
        return mix(spiralValue, ellipseValue, t);
        return -10.0;
    } else {
        return spiralValue;
    }

}


