      float tt = time/50000.0;

    float red = 0.5 + 0.5 * spiralToEllipse(p, goldenRatio, sin(2.5 - tt), 4.98, 0.35);

    //float green = 0.5 + 0.5 * spiralToEllipse(p,goldenRatio, 2.5 + tan(tt + tan(tt/30.)*1.2) *2.5 , 4.98, 0.35);
    float green =  spiralToEllipse(p,goldenRatio, 2.5 + tan(tt) *2.5, 4.98, 0.35);
    float blue = 0.5 + 0.5 * spiralToEllipse(p, goldenRatio, sin(2.5 + tt), 4.98, 0.35);

    float gray =0.5 + 0.5 * spiralToEllipse(p,goldenRatio, 4.0, 4.98, 0.41 );

