void mainImage( out vec4 color, vec2 coord )
{
    float radius = coord.y / iResolution.y * 1.25 + 0.1;
    float Pi = 3.14159265359;
    float t = 16.*iTime;
    float e = 50./iResolution.y;

	vec2 pos = ( coord+coord - iResolution.xy ) /iResolution.y * 8.;  

    pos.y += iTime;
    pos -= .5;   
    float v = ceil(pos.y);
    pos.x += .5*v;                                                   // hexagonal tiling

    pos = 2.*fract(pos)-1.;                                            // dots
	color = vec4( smoothstep(e,-e, length(pos)-radius) );  // O += .. -O no longer working

}
