vec3 randoColor(float seed){

    vec3 outColor = vec3(0.0, 0.0, 0.0);

    outColor.x = fract(sin(dot(vec2(74.275, 52.6345), vec2(3.1415 * seed, 1.618 * seed))) * 100.0 * seed);
    outColor.y = fract(sin(dot(vec2(15.479, 17.2942), vec2(3.1415 * seed, 1.618 * seed))) * 100.0 * seed);
    outColor.z = fract(sin(dot(vec2(35.442, 84.4167), vec2(3.1415 * seed, 1.618 * seed))) * 100.0 * seed);

    return outColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = randoColor(dot(uv, vec2(635.7734, 923.5523)));

    // Output to screen
    fragColor = vec4(col, 1.0);
}
