float hash11(float p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

float hash12(vec2 p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float hash13(vec3 p3) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.zyx + 31.32);
    return fract((p3.x + p3.y) * p3.z);
}

vec3 hash31(float p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

vec4 hash41(float p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    vec4 p4 = fract(vec4(p) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

vec4 hash42(vec2 p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    vec4 p4 = fract(vec4(p.xyxy) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

#define pi 3.1415926

mat3 yaw_pitch_roll(float yaw, float pitch, float roll)
{
    mat3 R = mat3(vec3(cos(yaw), sin(yaw), 0.0), vec3(-sin(yaw), cos(yaw), 0.0), vec3(0.0, 0.0, 1.0));
    mat3 S = mat3(vec3(1.0, 0.0, 0.0), vec3(0.0, cos(pitch), sin(pitch)), vec3(0.0, -sin(pitch), cos(pitch)));
    mat3 T = mat3(vec3(cos(roll), 0.0, sin(roll)), vec3(0.0, 1.0, 0.0), vec3(-sin(roll), 0.0, cos(roll)));

    return R * S * T;
}

float halfspace(vec3 p)
{
    return p.z;
}

float tube(vec3 p, float r, float h, float th)
{
    float d = abs(length(p.xy) - (r-th)) - th;
    //float d = length(p.xy) - r;
    // extrusion https://iquilezles.org/articles/distfunctions/
    vec2 w = vec2(d, abs(p.z) - h);
    return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}

#define EPSILON 0.001
#define DIST_MAX 50.0
#define ITER_MAX 200u

vec4 combine(vec4 a, vec4 b)
{
    return a.w < b.w ? a : b;
}

vec2 rotate(float phi, vec2 p)
{
    vec2 cs = vec2(cos(phi), sin(phi));
    return mat2(cs.x,cs.y,-cs.y,cs.x) * p;
}

vec3 rotate_z(float phi, vec3 p)
{
    return vec3(rotate(phi, p.xy), p.z);
}

vec2 polar(float phi)
{
    return vec2(cos(phi), sin(phi));
}

vec4 stack(vec3 p, float e, vec2 seed)
{
    float th = 0.05;
    float r = sqrt(3.0)/2.0;
    float d = r-th+0.5;
    float f = r-th-0.5;
    p.z -= e;
    vec4 h = hash42(seed);
    float phi = pi * 2.0 * h.w;
//h.xyz = vec3(0,1,2)/1.5/2.0;
    vec4 result = vec4(hash31(1.0+456.321*h.w), tube(p-vec3(0,0,e+e), r, e, th));
    result = combine(result, vec4(hash31(1.0+456.321*h.x), tube(rotate_z(phi, p-d*vec3(polar(0.0*pi/1.5),0)-f*vec3(polar(h.x*pi*2.0),0)), r, e, th)));
    result = combine(result, vec4(hash31(1.0+456.321*h.y), tube(rotate_z(phi, p-d*vec3(polar(1.0*pi/1.5),0)-f*vec3(polar(h.y*pi*2.0),0)), r, e, th)));
    result = combine(result, vec4(hash31(1.0+456.321*h.z), tube(rotate_z(phi, p-d*vec3(polar(2.0*pi/1.5),0)-f*vec3(polar(h.z*pi*2.0),0)), r, e, th)));
    return result;
}

vec4 map(ivec2 v, vec3 l) // unit grid: voxel = float(position), local = fract(position)
{
    vec4 result = vec4(vec3(0.5),l.z);
    {
        float h = hash12(vec2(v));
        if(h < 0.5)
        {
            //result = combine(result, vec4(1,1,1, box(l-0.5, vec3(0.5))));
            //result = combine(result, vec4(1,1,1, box(l-0.75, vec3(0.25))));
            //result = combine(result, vec4(0,0,0, box(l-0.25, vec3(0.25))));
            float s = 5.0; // TODO: exact value
            result = combine(result, stack((l-vec3(0.5,0.5,0))*s, 0.5, vec2(v))/s);
        }
    }

    return result;
}
#ifdef HIGH_QUALITY
vec4 map(vec3 p) // for correct ambient occlusion sample 2x2 voxels (slow!)
{
    // I think kastorp originally suggested to sample only 2x2 instead of 3x3, thanks!
    vec4 result = vec4(0,0,0,DIST_MAX);
    ivec2 s = ivec2(step(0.5, fract(p)));
    ivec2 o;
    for(o.y = s.y-1; o.y < s.y+1; o.y++)
        for(o.x = s.x-1; o.x < s.x+1; o.x++)
            result = combine(result, map(ivec2(floor(p.xy))+o, vec3(fract(p.xy)-vec2(o),p.z)));
    return result;
}
#else
vec4 map(vec3 p)
{
    return map(ivec2(floor(p.xy)), vec3(fract(p.xy),p.z));
}
#endif
// https://iquilezles.org/articles/normalsSDF tetrahedron normals
vec3 normal(vec3 p)
{
    const float h = EPSILON;
    const vec2 k = vec2(1,-1);
    return normalize(k.xyy*map(p + k.xyy*h).w +
                     k.yyx*map(p + k.yyx*h).w +
                     k.yxy*map(p + k.yxy*h).w +
                     k.xxx*map(p + k.xxx*h).w);
}

// NOTE: Apparently sign fails on some systems! Thanks to spalmer for debugging this!
vec3 sgn(vec3 v) // WORKAROUND FOR COMPILER ERROR on some systems
{
    return step(vec3(0), v) * 2.0 - 1.0;
}

vec4 trace(vec3 ro, vec3 rd, float t0, float t1) // ray-march sdf handling discontinuities between voxels  (jt)
{
    uint i;
    vec4 t = vec4(0,0,0,DIST_MAX);
    for(t.w = t0, i = 0u; t.w < t1 && i < ITER_MAX; i++) // finite loop originally suggested by pyBlob to avoid stalling if ray parallel to surface just above EPSILON
    {
        vec3 p = ro + rd * t.w;
        vec4 h = map(p);
        if(h.w < EPSILON)
            return vec4(h.xyz, t.w);

        // NOTE: An extra step per voxel, use if sdf discontinuous between voxels
        //       Could make this conditional by prefixing sth. like if(voxel_changed) // suggested by spalmer, see variant below
        // constrain step to voxels (voxel-snap ray-march plugin by jt, thanks to Shane for the idea!)
        {
            // NOTE: assuming unit grid
            // sgn(rd)*0.5 are the walls in ray direction, fract(p) - 0.5 is center of voxel
            vec3 sd = (sgn(rd)*0.5 - (fract(p) - 0.5))/rd; // distances to voxel sides / walls
            vec3 n = step(sd.xyz, min(sd.yzx, sd.zxy)); // component true if corresponding wall is nearest (at most one component true) NOTE: originally I used lessThanEqual, min from fb39ca4/kzy then switched to step, min by iq
            float skip = dot(sd, vec3(n)) + EPSILON; // distance to next voxel: sum up all components, weighted by the nearest flag (assuming only one component is true this selects the nearest component)
            h = min(h, skip); // constrain step to at most next voxel to handle sdf discontinuities between voxels
        }

        t = vec4(h.xyz, t.w + h.w);
    }

    return t; // stop on running out of iterations
    //return vec4(t.xyz,t1.w); // pass on running out of iterations
}

// NOTE: Don't forget to add +normal*EPSILON to the starting position
//       to avoid artifacts caused by getting stuck in the surface
//       due to starting at distance < EPSILON from the surface.
//       (normal could be calculated here but that would most likely be redundant)
//       Thanks to spalmer for pointing that out.
float shadow(vec3 ro, vec3 rd, float t0, float t1)
{
    return trace(ro, rd, t0, t1).w < t1 ? 0.0 : 1.0;
}

// https://iquilezles.org/articles/rmshadows
float softshadow(vec3 ro, in vec3 rd, float t0, float t1, float k)
{
    float res = 1.0;
    float ph = 1e20;
    uint i;
    float t;
    for(t = t0, i = 0u; t < t1 && i < ITER_MAX; i++)
    {
        float h = map(ro + rd*t).w;
        if( h < EPSILON )
            return 0.0;
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, k*d/max(0.0,t-y) );
        ph = h;
        t += h;
    }
    return res;
}

// https://www.shadertoy.com/view/Xds3zN raymarching primitives
float calcAO( in vec3 pos, in vec3 nor )
{
    float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map( pos + h*nor ).w;
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) ;
}

float checker(vec2 p)
{
    return step(0.0, sin(p.x*pi)*sin(p.y*pi));
}

float checker(vec3 p)
{
    return step(0.0, sin(p.x*pi)*sin(p.y*pi)*sin(p.z*pi));
}

vec3 material(vec3 p)
{
    return vec3(1.0);
    //return mix(vec3(0.25), vec3(0.75), checker(p*2.0+0.5));
}

void mainImage(out vec4 fragColor, vec2 I)
{
    bool demo = all(lessThan(iMouse.xy, vec2(10.0)));
    vec2 R = iResolution.xy;
    I = (2.0 * I - R) / R.y; // concise scaling thanks to Fabrice
    float yaw = 2.0 * pi * float(iMouse.x) / float(R.x);
    float pitch = pi - pi / 2.0 * float(iMouse.y) / float(R.y);
    yaw = !demo ? yaw : 2.0 * pi * fract(iTime * 0.01);
    pitch = !demo ? pitch : 4.0/3.0 * pi / 2.0;

    vec3 ro = vec3(0.0, 0.0,-5);
    vec3 rd = normalize(vec3(I.xy, 2.0));

    mat3 M = yaw_pitch_roll(yaw, pitch, 0.0);
    ro = M * ro;
    rd = M * rd;
    ro.z += 0.1;

    vec3 lightdir = normalize(vec3(3.0, 2.0, 1.0));
    vec3 sky_color = mix(vec3(0,0.5,0.5),vec3(0,0,1),abs(-rd.z));
    vec3 color = vec3(1);
    vec4 result = trace(ro, rd, 0.0, DIST_MAX);
    if(result.w < DIST_MAX)
    {
        float d = result.w;
        vec3 dst = ro + rd * d;
        vec3 n = normal(dst);

        //color *= (n * 0.5 + 0.5);
        color = result.xyz;
        color *= material(dst);

        vec3 ambient = vec3(0.4);
        float brightness = max(dot(lightdir, n), 0.0);
        if(brightness > 0.0)
            brightness *= shadow(ro + rd * d + n * 0.01, lightdir, 0.0, DIST_MAX);
            //brightness *= softshadow(ro + rd * dist + n * 0.01, lightdir, 0.0, DIST_MAX, 20.0);
        color *= (ambient * calcAO(dst, n) + brightness);
/*
        if(brightness > 0.0)
        {
            float specular = pow(max(0.0, dot(n, normalize(-rd + lightdir))), 250.0);
            color += specular;
        }
*/
        //vec3 fog_color = vec3(0.2);
        vec3 fog_color = sky_color;
        color = mix(fog_color, vec3(color), exp(-pow(d/20.0, 2.0))); // fog
    }
    else
    {
        float d = max(0.0, dot(rd, lightdir));
        float sun = smoothstep(0.998, 0.999, d) + exp(-pow((1.0-d)*500.0, 2.0)); // sun with halo
        color *= sky_color;
        color += sun * 2.0;
    }

    color = tanh(color); // roll-off overly bright colors
    fragColor = vec4(color, 1);
    fragColor = sqrt(fragColor); // approximate gamma
}
