#define PI 3.14159265358979323846264338327950288

mat3x2 base = mat3x2(
    vec2(1.  ,  0.         ),
    vec2(-0.5,  sqrt(3.)/2.),
    vec2(-0.5, -sqrt(3.)/2.));

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Centralized-Normalized pixel coordinates
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.yy;

    vec3 tri = uv*base*10.;

    float rel = dot(cos(tri), vec3(1.));
    float img = dot(sin(tri), vec3(1.));

    fragColor = vec4(
        length(vec2(rel, img))<=1.52+1.45*cos(uv.x*1.2-iTime),
        0., 0.,
        1.);
    fragColor /= 1.25;
    fragColor += 0.15;
}
