const float pi = acos(-1.);


void mainImage( out vec4 O, in vec2 F )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 R = iResolution.xy,
         u = (F+F-R) / R.y;

    float l = length(u);
    float v1 = sin(u.x * 10.0 + iTime);
    float v2 = sin(5.0 *( u.x * sin(iTime/2.0)+u.y * cos(iTime/3.0))+iTime);
    vec2  c = u + sin(iTime / vec2(5,3))*.75;
    float v3 = sin( 5.*length(c)+iTime);

    float vf = v1 + v2 + v3;

    //float r  = cos(vf*pi),
    //      g  = sin(vf*pi + 2.*pi),
    //      b  = cos(vf*pi + 4.0*pi/3.0);

    vec3 rgb = cos(vf*pi + vec3(cos(vf*pi), sin(vf*pi + 2.*pi), cos(vf*pi + 4.0*pi/3.0)));     
    O = vf + pow(.18 /  ( vf = fract( vf * 6.5) - .75) / exp(l), 1.2)//pow
    //palette
    * vec4(rgb, 1.0)* 2.3; //* (1. + cos(6. * ( l + (i + iTime)*.4 + vec4(.26,.4,.56,0) )));


}
