void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv = 20. * uv - 10.;

    // Time varying pixel color
    float r = (
        smoothstep(0., 1., 0.7 * sin(1. * iTime) + fract(iTime + length(uv)))
    );

    float g = (
        smoothstep(0., 1., 0.7 * sin(2. * iTime) + fract(iTime - length(uv)))
    );

    float b = (
        smoothstep(0., 1., 0.7 * sin(3. * iTime) + fract(iTime + length(uv)))
    );


    // Output to screen
    fragColor = vec4(r, g, b, 1.0);
}
