#define R(a) mix(q*dot(p,q), p, cos(a)) + sin(a)*cross(p,q)

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,q=vec3(-1,0,1);
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e);
    //p.z-=.3;
    p=reflect(p,q);
    p=R(cos(iTime)/4.);

    for(j=0.;j++<4.;)
        p=abs(abs(mod(p,.4))-.002)-.001,
        d=min(min(p.z,p.y),p.x)/4.,
        e+=d;

}o+=vec4(-log(p),1)/i;
}
