#define I iResolution

void mainImage( out vec4 U, in vec2 V )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 u = ( V * 2. - I.xy ) / I.y;

    // Time varying pixel color
    float p = .1, i;

    U = vec4(0);

    while( i++<40. )
        p = min(
            p,
            fract( length(u - vec2( cos( i + iTime ), sin( i + iTime ) ) ) - iTime * .5 )
        ),
        U.rgb += smoothstep( 1e-2, 1e-3, p );

