void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

    vec2 particlePos = vec2(0.5, 0.5);

    float time = iTime;

    particlePos += normalize(vec2(sin(time), cos(time))) * 0.01;

    float shape = sin(length(uv - particlePos) * 10.0) * 0.2 + 1.0;

    vec3 color = vec3(sin(time), cos(time), 0.5);

    fragColor = vec4(color * shape, 1.0);
}
