// variant of generalized solenoid https://shadertoy.com/view/dlVGDW

#define rot2(a)      mat2(cos(a+vec4(0,11,33,0)))                 // rotation

vec3 S = vec3(3,2,0);                                             // knot parameter

//#define L(p) length(p) - .05                                    // cross section. 2nd = Shane squarish
//#define L(p) ( q.xy = abs(p) - .05 + .02, min(max(q.x, q.y), 0.) + length(max(q.xy, 0.)) - .02 )
  #define L(p) int(iTime)%4 < 2 ? length(p) - .05 : ( q.xy = abs(p) - .05 + .02, min(max(q.x, q.y), 0.) + length(max(q.xy, 0.)) - .02 )

float sdf(vec3 q) {
        q = vec3( length(q.xz)-.5, q.y, atan(q.z,q.x)-.5*iTime ), // cylindrical coordinates
        q = vec3( length(q.xy), atan(q.y,q.x), q.z );             // torus coordinates
        return L( vec2(q.x-.2, ( mod(S.x*q.z-S.y*q.y,6.28)-3.14 ) /16. )); // solenoïd
}

void mainImage(out vec4 O, vec2 U)
{
    float t=9.,r;
    vec3  R = iResolution, e = vec3(1e-3,0,0), N,
          D = normalize(vec3(U+U, -18.*R.y) - R),          // ray direction
          p = vec3(0,0,15), q,                             // marching point along ray 
          C = iMouse.z > 0. ? 8.*iMouse.xyz/R -4.             // camera control
                          : 3.* cos(.3*iTime + vec3(0,11,0)); // demo mode
       // S = ceil( vec3( 5.*iTime+vec2(1,0),0)   );

    p.yz *= rot2(-C.y),                                    // rotations
    p.xz *= rot2(-C.x-1.57),
    D.yz *= rot2(-C.y),                              
    D.xz *= rot2(-C.x-1.57);
    for ( O=vec4(1); O.x > 0. && t > .01; O-=.01 )         // march scene
        q = p, 
        t = min(t, sdf(q) ),                               // solenoïd
        p += .5*t*D;                                       // step forward = dist to obj    

    N = vec3( sdf(q+e), sdf(q+e.yxy), sdf(q+e.yyx) ) - t ; // normal
    O.x < 0. ? O = .5*texture(iChannel0, D) :              // uncomment to display environment 
    O *= O*O*O*3.* texture(iChannel0, reflect(D,N/length(N) ) ); // reflect of environment map
}
