void mainImage(out vec4 O, vec2 I )
{
    //Approximate isometric coordinates + scrolling
    vec3 p = I*mat3x2(2,1,2,-1,0,-2)/.2/iResolution.y+iTime;
    //Also try mat3x2(2,1,-2,1,0,-2)

    //Gyroid
    O = cos(dot(sin(p.yzx),cos(p))/.4)/vec4(2)+.5;
    //Chromatic aberration
    O+=dFdx(O)*vec4(8,4,0,0);
}
