void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec2 uv = (fragCoord)/iResolution.y*2.,
    offset = vec2(0);
    vec3 col = vec3(0);
    vec2 uv1 = uv;
    for(int k = 0; k < 6; k++){
        offset =
            abs(fract(vec2(-uv.x-.5,uv.y)-floor(iTime)/16./16.)-.5)
        ;
        uv =
            abs(fract(uv.yx-offset-floor(iTime)/16.)-.5)
        ;
        if(uv.x > uv.y)col = vec3(col.yz,abs(uv.x-uv.y));
    }  
    fragColor = vec4(min(col*2.,vec3(1.)),1.0);
}
