float sdCircle(vec2 uv, float r)
{
    return length(uv) - r;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);

    vec2 p = vec2(atan(uv.y, uv.x), length(uv));

    float d = sdCircle(uv, 0.01);
    float f = cos(sin(iTime)/p.y) * cos(iTime + 7. * p.x);
    f = f + p.y * 2. * d;
    f = smoothstep(0.1, 1.0, f);

    float t = (sin(iTime) + 1.) / 2.;
    vec3 col = vec3(1.0 - f);

    col = mix(vec3(0.8, 0.8, 0.9), col, f);
    col = mix(vec3(0.5 * t, 0.1 + t / 3., 0.5 * t), col, 1.0 - f);
    col = mix(vec3(0.36 * t, 0.30 + t / 2., 0.24 * t), col, smoothstep(0.12, 0.22, d));
    fragColor = vec4(col, 1.0);
}
