void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //ignore the comments and reasd my shitty code
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.y = uv.y*2.0-1.0;
    vec3 c = vec3((sin(uv.x*3.0)*(uv.y)),0.0,(sin(uv.x*3.0)*(uv.y)*-1.0));
    // Time varying pixel color
    float multi = abs(sin(iTime*0.1))*35.0+3.0;
    uv.x+=iTime*0.03;
    float r =  1.0/iResolution.y*2.5*multi;
    if(sin(uv.x*multi)-r<=uv.y && sin(uv.x*multi)+r>=uv.y) c =vec3(1.0,1.0,1.0);
    // Output to screen
    fragColor = vec4(c,1.0);
}
