// original: https://youtu.be/f4s1h2YETNY

//https://iquilezles.org/articles/palettes/
vec3 palette( float t ) {
    float spring = (sin(iTime*1.0) + sin(iTime*1.3)+ sin(iTime*1.7)) * 10.5;

    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.163 * spring,0.116 * spring,0.157 * spring);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    float spring = (sin(iTime*1.0) + sin(iTime*1.3)+ sin(iTime*1.7)) * 1.5;

    for (float i = 0.0; i < abs(spring); i++) {
        uv = fract(uv * 1.5) - 0.5;

        float d = length(uv*uv) * exp(-length(uv0));

        vec3 col = palette(length(uv0) + i*.6 + iTime*1.6);

        d = sin(d*16. + iTime)/8.;
        d = abs(d);

        d = pow(0.01 / d, 1.2);

        finalColor += col * d;
    }

    fragColor = vec4(finalColor, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 texel = 1. / iResolution.xy;

    float spring = (sin(iTime*1.0) + sin(iTime*1.3)+ sin(iTime*1.7)) * 0.02;

    float step_y = texel.y + spring;
    float step_x = texel.x + spring;
    vec2 s  = vec2(0.0, step_y);
    vec2 n  = vec2(0.0, step_x);

    vec4 im_n =  texture(iChannel0, uv+n);
    vec4 im =    texture(iChannel0, uv);
    vec4 im_s =  texture(iChannel0, uv+s);

    // use luminance for sorting
    float len_n = dot(im_n, vec4(0.299, 0.587, 0.114, 0.));
    float len = dot(im, vec4(0.299, 0.587, 0.114, 0.));
    float len_s = dot(im_s, vec4(0.299, 0.587, 0.114, 0.));

    if(int(mod(float(iFrame) + fragCoord.y, 2.0)) == 0) {
        if ((len_s > len)) { 
            im = im_s;    
        }
    } else {
        if ((len_n < len)) { 
            im = im_n;    
        }   
    }

    // blend with image
    if(iFrame<1) {
        fragColor = texture(iChannel0, uv);
    } else {
        fragColor = (texture(iChannel0, uv) + im * 99. ) / 100.;
    }

}

precision mediump float;

#define TWOPI 6.28318530718

vec3 mod289(vec3 x) {
    return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec4 mod289(vec4 x) {
    return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec4 permute(vec4 x) {
    return mod289(((x * 34.0) + 10.0) * x);
}

vec4 taylorInvSqrt(vec4 r) {
    return 1.79284291400159 - 0.85373472095314 * r;
}

float snoise(vec3 v) {
    const vec2 C = vec2(1.0 / 6.0, 1.0 / 3.0);
    const vec4 D = vec4(0.0, 0.5, 1.0, 2.0);

    // First corner
    vec3 i = floor(v + dot(v, C.yyy));
    vec3 x0 = v - i + dot(i, C.xxx);

    // Other corners
    vec3 g = step(x0.yzx, x0.xyz);
    vec3 l = vec3(1.0) - g;
    vec3 i1 = min(g.xyz, l.zxy);
    vec3 i2 = max(g.xyz, l.zxy);

    vec3 x1 = x0 - i1 + C.xxx;
    vec3 x2 = x0 - i2 + C.yyy;
    vec3 x3 = x0 - D.yyy;

    i = mod289(i);
    vec4 p = permute(permute(permute(i.z + vec4(0.0, i1.z, i2.z, 1.0)) + i.y + vec4(0.0, i1.y, i2.y, 1.0)) + i.x + vec4(0.0, i1.x, i2.x, 1.0));

    float n_ = 0.142857142857;
    vec3 ns = n_ * D.wyz - D.xzx;

    vec4 j = p - 49.0 * floor(p * ns.z * ns.z);

    vec4 x_ = floor(j * ns.z);
    vec4 y_ = floor(j - 7.0 * x_);

    vec4 x = x_ * ns.x + ns.yyyy;
    vec4 y = y_ * ns.x + ns.yyyy;
    vec4 h = vec4(1.0) - abs(x) - abs(y);

    vec4 b0 = vec4(x.xy, y.xy);
    vec4 b1 = vec4(x.zw, y.zw);

    vec4 s0 = floor(b0) * 2.0 + 1.0;
    vec4 s1 = floor(b1) * 2.0 + 1.0;
    vec4 sh = -step(h, vec4(0.0));

    vec4 a0 = b0.xzyw + s0.xzyw * sh.xxyy;
    vec4 a1 = b1.xzyw + s1.xzyw * sh.zzww;

    vec3 p0 = vec3(a0.xy, h.x);
    vec3 p1 = vec3(a0.zw, h.y);
    vec3 p2 = vec3(a1.xy, h.z);
    vec3 p3 = vec3(a1.zw, h.w);

    // Normalise gradients
    vec4 norm = taylorInvSqrt(vec4(dot(p0, p0), dot(p1, p1), dot(p2, p2), dot(p3, p3)));
    p0 *= norm.x;
    p1 *= norm.y;
    p2 *= norm.z;
    p3 *= norm.w;

    // Mix final noise value
    vec4 m = max(0.5 - vec4(dot(x0, x0), dot(x1, x1), dot(x2, x2), dot(x3, x3)), 0.0);
    m = m * m;

    return 105.0 * dot(m * m, vec4(dot(p0, x0), dot(p1, x1), dot(p2, x2), dot(p3, x3)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = fragCoord.xy / iResolution.xy;
    //texture is loaded upside down and backwards by default, so flip it
    coord.y = 1.0 - coord.y;
    // disable screen fit

    float spring = 1.+(1.+sin(iTime*1.0) + 1.+sin(iTime*1.3)+ 1.+sin(iTime*1.7)) * 0.1;

    float noiseStrength = 0.01 * spring;
    float noiseScale = 0.002 * spring;

    float speed = 0.0005 * spring;
    float noiseTime = iTime * speed;

    float noise = snoise(vec3(
        (fragCoord.x - iResolution.x/2.) * noiseScale,
        (fragCoord.y - iResolution.y/2.) * noiseScale,
        noiseTime
    ));


    vec4 texture = texture(
        iChannel1, 
        vec2(
            fract(coord).x + noiseStrength * sin(noise * TWOPI),
            fract(coord).y + noiseStrength * cos(noise * TWOPI)
        )
    ); 

    fragColor = texture;
}
