void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( 2.*u - R ) / R.y;

    U.y = log2(1.-U.y) - iTime;
    U *= 10.;

    vec2 I = floor( U ),
         F = fract( U +.5 ) -.5;

    F = smoothstep( 0.,1., abs(F) / min(fwidth(F),.4) ); 
    O = vec4(min(F.x,F.y));                               // lines
    O.r = mod(I.x+I.y,2.);                                // checker
    O = mix(O, vec4(.5,1,1,1)*.5, smoothstep(0.,.5,fwidth(U.y)));  // fading to mid color
}
