void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col;
    vec2 uv = fragCoord/iResolution.xy;
    uv.y *= iResolution.x/iResolution.y;
    uv += 0.5;

    vec2 mouse = iMouse.xy/iResolution.xy;
    //mouse.y -= 0.0; 
    mouse.y *= iResolution.x/iResolution.y;

    vec3 a = vec3( mix(-mouse.y, 1.0 - mouse.y, uv.y) );
    vec3 b = vec3( mix(1.0 + mouse.y, mouse.y, uv.y) );
    /*vec3 a = vec3( mix(1.0, 0.0, uv.y-mouse.y) );
    vec3 b = vec3( mix(0.0, 1.0, uv.y-mouse.y) );*/


    col = (b*a*4.0)*(1.+mouse.x*100.)-mouse.x*100.;
    //col=b;
    fragColor = vec4(col,1.0);
}
