#define R iResolution.xy
#define T (iTime)
#define M_PI (3.1416)
#define TAU (M_PI * 2.0)
mat2 rot(float a) { float s = sin(a); float c = cos(a); return mat2(c, s, -s, c); }
void mainImage( out vec4 O, in vec2 fc ) {
    vec2 uv = (fc-0.5*R.xy)/R.y;
    vec2 m = (iMouse.xy-0.5*R.xy)/R.y;
    vec3 rd = normalize(vec3(uv.xy, 1.0));
    if (iMouse.z > 0.01) { rd.yz *= rot(m.y*TAU); rd.xz *= rot(m.x*TAU); }
    else { rd.xz *= rot(T*0.25); }
    O = vec4(mix(vec3(0.),
        texture(iChannel3, (rd.xz/rd.y)).rgb,
        clamp(max(0.0, dot(rd, vec3(0.0, -1.0, 0.0)))*4., 0.0, 1.0)), 1.0);
}
