float circle(vec2 st, float r)
{
    float d = distance(st,vec2(0.5));
    return smoothstep(r, r-.01, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;

    float blink = abs(cos(iTime));

    float pct = 0.0;
    pct = circle(st + vec2(.16, -.14), .06) // left eye
    	+ circle(st + vec2(-.16, -.14), .06) // right eye
    	+ circle(st, .05) // nose
        + circle(st + vec2(0., .1), .1) // mouth
        - circle(st + vec2(0.,0.05), .1)  // nose/mouth background
        - circle(st + vec2(.16, -.14 - blink), .06)  // left blink
        - circle(st + vec2(-.16, -.14 - blink), .06);  // right blink

    vec3 color = vec3(pct);

	fragColor = vec4( color, 1.0 );
}
