void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y*4.;
    vec2 tile = floor(uv);
    float tileId = tile.y * 10.0 + tile.x;
    float hash = fract(234.0 * abs(sin(tileId * 1324.0)));
    uv.x = fract(uv.x);
    uv.y = fract(uv.y);

    vec2 center = vec2(0.5, 0.5);

    float phase = hash*2.0*3.1415926;
    float freq = mix(7.0, 10.0, hash);

    float dist = distance(uv, center) - mix(0.1, 0.3, sin(freq * iTime + phase)*0.5+0.5);

    vec3 background = vec3(1.0, 1.0, 1.0);
    vec3 foreground = vec3(1.0, 0.0, 0.0);

    vec3 col = mix(background, foreground, smoothstep(0.01, -0.01, dist));

    //vec3 col = vec3(uv.x, uv.y, 0.0);

    fragColor = vec4(col, 1.0);
}
