void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_fc_nooff = fragCoord.xy/ iResolution.xy;


    float n_it_one_nor = 1./n_its;
    vec2 o_it = floor(o_fc_nooff/n_it_one_nor);

    vec4 o_data_mouse = texelFetch(iChannel0, ivec2(o_it.x, 0), 0);
    float n_d = float(o_fc_nooff.y > o_data_mouse.y);

    fragColor = vec4(n_d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float b_mousedown = iMouse.z;

    vec2 o_fc = (fragCoord - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_fc_nooff = (fragCoord.xy)/ iResolution.xy;
    vec2 o_mc = (iMouse.xy - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_mc_nooff = (iMouse.xy )/ iResolution.xy;


    if(iMouse.w == 0.){
        o_mc_nooff = vec2(sin(iTime)*0.5+0.5, cos(iTime)*0.5+0.5);
    }
    vec4 o_col_last = texture(iChannel0, o_fc_nooff);


    float n_it_one_nor = 1./n_its;
    vec2 o_it = floor(o_fc_nooff/n_it_one_nor);
    vec2 o_itm = floor(o_mc_nooff/n_it_one_nor);

    ivec2 o_iMouse_location = ivec2(o_itm.x, 0);


    fragColor = o_col_last;    

    if(ivec2(fragCoord.xy) == ivec2(o_iMouse_location)){
        fragColor = o_mc_nooff.xyxy;
    }
    //fragColor = vec4(o_itm.x/n_its);

}
