vec4 plot(in float val, vec2 u)
{
    vec3 col;
    float a = 1.;
    if(abs(u.y - val) < 0.03){
        float chaser = fract(u.x*0.5 - 0.8*iTime);
        if (chaser > 0.99)
        {
            chaser = 1.;
        }
        else chaser = max(min(chaser, 0.6), 0.3);
        float d = 1. - abs(u.y - val)/0.03;
        col.z = col.y = col.x = chaser * d;
    }
    return vec4(col, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u = (fragCoord/iResolution.xy - 0.5) * 2.;
    float val = 0.8*sin(8.*u.x - iTime) + 0.01*sin(50.*u.x - 100.*iTime);
    fragColor = plot(val, u);
}
