vec3 clrDist(float dist)
{
    vec3 col = dist>0.0 ? vec3(0.9,0.6,0.3) : vec3(0.65,0.85,1.0);
    col *= 1. - exp(-6.*abs(dist));
    col *= 1. + 0.2*sin(150.*abs(dist));
    col = mix(col, vec3(1.), smoothstep(0.01, 0., abs(dist)));
    return col;
}

float sdPoint(vec2 p, float r){
    return length(p) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    float scale=min(iResolution.x, iResolution.y);

    vec2 p = (2.*fragCoord-iResolution.xy)/scale;
    float dist=sdPoint(p, sin(iTime)/2.+.5);
    vec3 clr=clrDist(dist);

    fragColor=vec4(clr, 1.);
}
