void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
 vec2 m = vec2 (-.1, .5);//iMouse.xy/iResolution.xy;
 //float zoom = pow(10., -m.y*3.);
 float t = sin(float(m));
     t = (iTime, .5);


 vec2 uv = (fragCoord.xy -.5*iResolution.xy) / iResolution.y;

 vec2 c = uv*t*3.;
 c += vec2(-.8, 0);

 vec2 z = vec2(0.);
 float iter = 0.;

 const float max_iter =55.;


 for(float i=0. ; i<max_iter; i++) {
     z = vec2(z.x*z.x - z.y*z.y, 2.*z.x*z.y) + c;

     if(length(z)>2.)break;

     iter++;
 }

 float f = iter/max_iter;
     f += sin(iTime) + .3;

 vec3 col = vec3(f,f*.5,.24);
 fragColor = vec4(col, .5);
}
