void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    vec2 ab = (fragCoord * 2.0 - iResolution.xy)/iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(0.,0.,0.);
    col += .0;


    vec2 pt = vec2(0., 0.);
    pt.x += cos(4.*iTime);
    pt.y += sin(4.*iTime);
    //col.rg += 0.1/length(ab - pt);


    //col.b += .01/length(ab.x-pt.x/2.);
    //col.r += .01/length(ab.y-pt.x);

    col += 0.03/(1.-
            ( step(1.,
                //length(2.+2.*cos(mod(iTime,3.1415))+ab) ) * step(0.,1.-length(ab)))
                length(1.5*cos(iTime)+ab))* step(0.,1.-length(ab)))
            );


    // Output to screen
    fragColor = vec4(col,1.0);
}
