const float pi=4.*atan(1.);
const float ang=(3.-sqrt(5.))*pi;


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    float R=10.;

    fragCoord/=iResolution.xy;
    vec3 o;

    for(float dy=-R;dy<R;dy+=1.0){
        for(float dx=-R;dx<R;dx+=1.0){
            float dis=sqrt(dy*dy+dx*dx);
            if(dis<R){
            o+=pow(texture(iChannel0,fragCoord+vec2(dx,dy)/iResolution.xy).rgb,vec3(gamma));
            }
        }
    }
    o=pow(o/(pi*R*R),vec3(1./gamma));

    fragColor=vec4(o,1.);
}

