vec4 f_o_col_char(vec2 o_trn_fc_nor, vec2 o_char){

    vec2 o_scl_chars = vec2(16.);
    vec2 o = (o_trn_fc_nor.xy/o_scl_chars.xy)+vec2(1./16.)*o_char;
    vec4 o_col = texture(iChannel0, o);
    return o_col;
}

float f_n_rand(float n){
    n = n*0.338;
    n = n+n;
    n = sin(n*231.234)*1223.23;
    return fract(n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float n_ratio_x_to_y = iResolution.x / iResolution.y; 

    float n = 40.; 
    vec2 o_tres  = vec2(n*n_ratio_x_to_y, n);//target resolution
    vec2 o_scl_tres = o_tres / iResolution.xy;
    vec2 o_ires = iResolution.xy * o_scl_tres;
    vec2 o_fc = fragCoord.xy * o_scl_tres.xy;
    //o_fc += iTime;
    float n_idx_fc = (floor(o_fc.x) + floor(o_fc.y)*floor(o_tres.x));// index
    float n_idx_fc_nor = n_idx_fc / floor(o_tres.x*o_tres.y);

    float n_ratio_1to_iresx = 1./ o_ires.x;//we can make a lowres AA
    float n_ratio_1to_iresolutionx = 1./ iResolution.x;//we need the real resolution for AA
    //needed for anti aliasing ?

    float n_speed_max = 10.;
    float n_speed_min = 5.;
    float n_random_per_x = f_n_rand(floor(o_fc.x));
    o_fc.y += iTime*(n_random_per_x*n_speed_max+n_speed_min);
    vec2 o_trn_fc_nor = o_fc.xy / o_ires.xy;
    vec2 o_trn_mo_nor = iMouse.xy / iResolution.xy;


    vec2 o_trn_fc_nor_offset = (o_trn_fc_nor - vec2(0.5))*vec2(n_ratio_x_to_y, 1.);
    vec2 o_trn_mo_nor_offset = (o_trn_mo_nor - vec2(0.5))*vec2(n_ratio_x_to_y, 1.);


    vec2 o_scl_letters = vec2(16.,16.);

    vec2 o_trn_char = vec2(
        f_n_rand(floor(o_fc.x))*o_scl_letters.x, 
        f_n_rand(floor(o_fc.y))*o_scl_letters.y
    );
    float b_showx = float(n_random_per_x > 0.5);
    float b_showy = float(f_n_rand(floor(o_fc.y)));

    vec4 o_col_char = f_o_col_char(fract(o_fc), floor(o_trn_char));

    o_col_char *= b_showy;
    fragColor = o_col_char.xxxx*vec4(0., 1.,0.,1.);

    //fragColor = vec4(texture(iChannel0, fragCoord.xy/iResolution.xy).r);
}
