void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy / iResolution.xy)*2.0-1.0;

    //3D position of the lines
    vec3 pos = vec3(uv, sin((uv.x + iTime*0.5)*10.0)*0.5);

    //Calculate the distance to the lines
    float dist = distance(pos, vec3(0));

    //Mix colors based on the distance
    vec3 color = mix(vec3(1.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0), dist);

    //Output the color
    fragColor = vec4(color, 1.0);
}
