void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;

    uv -= .5; //horizontal offset
    uv.x *= iResolution.x/iResolution.y; //correct aspect ratio
    float d = length(uv); //distance from origin
    float s = 8.4; //scale (zoom)

    //red, green, blue intensity based on distance from origin, x and y 
    float r=abs(.8*tan(cos(s*uv.x*3.)+cos(s*uv.y*3.)));
    float g=1.6*tan(cos(s*uv.x)+cos(s*uv.y));
    float b=1.6-.8*tan(cos(s*uv.x)+cos(s*uv.y));

    //intensities beolw 0 all count as 0 and above 1 all count as 1
    //modulo 1 intensities for the values 1 to 32
    if (r>1.0 && r<32.) r=r-(1.*floor(r/1.));
    if (g>1.0 && g<32.) g=g-(1.*floor(g/1.));
    if (b>1.0 && b<32.) b=b-(1.*floor(b/1.));

    // Time varying pixel color
    vec3 col = cos(iTime/1.5+(1.-d)*16.+vec3(r,g,b));

    // Output to screen
    fragColor = vec4(col,1.0);
}
