void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;

    uv -= .5; //horizontal offset
    uv.x *= iResolution.x/iResolution.y; //correct aspect ratio
    float d = length(uv); //distance from origin
    float s = 20.; //scale (zoom)

    float r=0.8*tan(cos(s*uv.x*3.)+cos(s*uv.y*3.));
    float g=1.6*tan(cos(s*uv.x)+cos(s*uv.y));
    float b=1.6-0.8*tan(cos(s*uv.x)+cos(s*uv.y));

    // Time varying pixel color
    vec3 col = cos(iTime*1.5+(1.-d)*180.+vec3(r,g,b));

    // Output to screen
    fragColor = vec4(col,1.0);
}
