float Circle(vec2 uv, vec2 pos, float size, float blur){
   float d = length(uv - pos);
   float c = smoothstep(size, size-blur,d);
   return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float asp = iResolution.x / iResolution.y;
    uv.x *= asp;    

    vec2 mUv = iMouse.xy/iResolution.xy;

    float mask = texture(iChannel0, uv).r;

    // change color
    vec3 color = vec3(1.,1.,1.);

    float circle = Circle(uv, vec2(mUv.x*asp, mUv.y), fract(iTime*0.1)*0.4, 0.01);
    float circle2 = Circle(uv, vec2(mUv.x*asp, mUv.y), fract(iTime*0.1)*0.8, 0.4);
    float circle3 = Circle(uv, vec2(mUv.x*asp, mUv.y), .6, 0.5)*mask;
    // Time varying pixel color
    vec3 col = color*(circle-(circle2*circle2))/circle3;

    // Output to screen
    fragColor = vec4(col,1.0);
}
