float ringer(in float t, in float d) {
    float r1 = fract(t/5.)*1.1;
    float r2 = fract((t-.25)/5.)*1.1;
    float ball1 = step(r1, d);
    float ball2 = 1.-step(r2, d);

    return(ball1 + ball2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    float ball = distance(uv,uv/2.);    
    float ring = ringer(iTime, ball);

    for (float i = 0.; i < 10.; i += .5) {    
        ring *= ringer(iTime-i, ball);
    }

    vec3 col = vec3(ring);

    fragColor = vec4(col,1.0);
}
