void mainImage( out vec4 rgba, in vec2 xy)
{
    // forked from https://www.shadertoy.com/view/NtyXRV

    // 6 ticks , 1 snap per 1 second (floor) , range [ 0.,1. ] 
    float timer_6 = ( mod( floor( iTime ) , 6. ) / 5. );

    float t = iTime;

    float _zoom = 1.5 +  2. * timer_6;

    vec2 uv = xy;

    uv *= 1.001; // fix flickerig  


    float mx = iMouse.x / iResolution.x;
    float my = iMouse.y / iResolution.y; 

    // uncomment to zoom in super close 
    // uv *= ( 1. - mx );

    // uncomment to zoom out super far 
    // uv *= ( 1. + mx * 1000. ); 

    float x=uv.x,y=uv.y;

    float turn=radians(360.0);

    float cR = clamp(sin((x*x+y*y)/_zoom-turn/2.0*t),0.0,1.0); // sin
    float cG = clamp(cos((x*x+y*y)/_zoom-turn/2.0*t),0.0,1.0); // cos 
    float cB = clamp(tan((x*x+y*y)/_zoom-turn/2.0*t),0.0,1.0); // tan

    float c = max ( cR, cG ) ; c = max ( c , cB ) ;
    float s = ( cR + cG + cB ) / 3. ;

    float brightness = 0.8;

    rgba = vec4( cR , cG, cB , 1 );

    //rgba = vec4( c , c, c , 1 );

    rgba = rgba * brightness;
}
