void mainImage( out vec4 fragColor, in vec2 coord ) {
    vec2 ruv = (coord-iResolution.xy*0.5)/min(iResolution.x,iResolution.y);
    for (float i = 1.; i < 8.; i++) {
        float d = smoothstep(.05,0.,abs(sin((length(fract(ruv*i)*2.-1.)-iTime*0.4)*5.)/5.));
        fragColor.rgb = (fragColor.rgb+vec3(d*d*d,d*0.3,0.5*d))*1.2;
    }
}
