const float PI   =   3.141592;
const float pot  =    .5;
const float freq = 50000.;


const vec3 a = vec3(  .086,   .086,   .109); // bgcolor: eigengrau


const vec3 b = vec3(  .051,  .053, .150); 
const vec3 c = vec3(  .052,  .361, .064);
const vec3 d = vec3(  .003,  .103, .013);

vec3 palette( float t ) {
    return a + b*cos(t*t*t) + c*sin(t*t) + d*cos(t);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float yRat = (iResolution.y/iResolution.x);

    vec2 uv = 2.*(fragCoord/iResolution.x - vec2(.5,yRat/2.));

    float x = uv.x;
    float y = uv.y;

    float valxy = 
        1. -
         pow( (1.+sin((2.*PI)*freq*sin(.00001*iTime)*(x+y)))/2., pot) * 
         pow( (1.+cos((2.*PI)*freq*cos(.00001*iTime)*(y-x)))/2., pot);


    vec3 col = palette(log(valxy)/.1);
    fragColor = vec4(col,1.0);
}
