const float fov = 1.0;
const float landHeight = 2.0;
const float landRepeat = 0.05;

struct Ray
{
    vec3 Position;
    vec3 Direction; // The length of direction must always be 1
};

vec3 getNormal(Ray ray)
{
    vec3 myRayPos_z = ray.Position;
    myRayPos_z.z += 0.04;

    myRayPos_z.y = texture(iChannel0, myRayPos_z.xz * landRepeat).x * landHeight;

    vec3 myRayPos_x = ray.Position;
    myRayPos_x.x += 0.04;

    myRayPos_x.y = texture(iChannel0, myRayPos_x.xz * landRepeat).x * landHeight;

    vec3 outNormal = cross(myRayPos_z - ray.Position, myRayPos_x - ray.Position);

    return normalize(outNormal);
}

vec3 rayTrace(Ray ray)
{
    vec3 outColor = texture(iChannel1, ray.Direction).xyz;
    for (int i = 0;i < 2000;i++)
    {
        if (ray.Position.z > 0.2)
        {
            float height = texture(iChannel0, ray.Position.xz * landRepeat).x * landHeight;
            if (height > ray.Position.y)
            {
                vec3 normal = getNormal(ray);

                float lums = dot(normal, vec3(cos(iTime), 1.0, sin(iTime))) * 0.5 + 0.5;
                outColor = texture(iChannel2, ray.Position.xz).xyz * lums;
                break;
            }
        }
        ray.Position += ray.Direction * 0.01;
    }
    return outColor;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

    Ray ray;
    ray.Position = vec3(iMouse.x / iResolution.x + 1.0, iMouse.y / iResolution.y + 1.5, iTime);
    ray.Direction = vec3(uv - 0.5, fov);

    vec3 outColor = rayTrace(ray);

    fragColor = vec4(outColor, 1.0);
}
